import { getJobs, showJobs } from '../actions/jobActions'

const initialState = {
    items: [],
    items: {}
}

export const jobReducer = (state=initialState, action) => {
    switch(action.type) {

      case FETCH_POSTS:
        return {
          ...state,
          items: action.payload
        };


       default: 
       return state
    }
}
